import React, {useState} from "react";
import axios from "axios";
import MostPopularProducts from "./ProductPage/MostPopulerProducts";
import "./Css/Product.css";

function Products() {

    const [search, setSearch] = useState('');
    const [SearchArray, updateSearchArray] = useState([]);
    const [error, setError] = useState('');

    const handleSearchChange = (e) => {
        setSearch(e.target.value);
    };

    const StartSearch = () => {
        axios.get('http://localhost:8080/products/search/' + search)
            .then(res => {
                updateSearchArray(res.data);
            })
            .catch(err => {
                setError(err.message);
            });
    };

    if(SearchArray != null) {
        return (
            <div className="container">
                <label>Search:</label>
                <input type="text" placeholder="Search" name={"SearchBar"} value={search} onChange={handleSearchChange} required/>

                <button type="submit" onClick={StartSearch}>start search</button>

                <h2>Found result</h2>
                {SearchArray.map(product => (
                    <div  className="card">
                        <img src="" alt="img" style="width:100%"/>
                        <h1>product.name1</h1>
                        <h3>product.name2</h3>
                        <p>product.genre</p>

                        <h2>Star Rating</h2>
                        <span className="fa fa-star checked"></span>
                        <span className="fa fa-star checked"></span>
                        <span className="fa fa-star checked"></span>
                        <span className="fa fa-star"></span>
                        <span className="fa fa-star"></span>
                        <p> product.description </p>
                        <button>View details</button>
                    </div>
                    )
                )}
            </div>
        );
    }
    else if(error != null){
        return (
            <div className="container">
                <label>Search:</label>
                <input type="text" placeholder="Search" name={"SearchBar"} value={search} onChange={handleSearchChange} required/>

                <button type="submit" onClick={StartSearch}>start search</button>

                <h3> Error: {error}</h3>
            </div>
        );
    }
    else{
        return (
            <div className="container">
                <label>Search:</label>
                <input type="text" placeholder="Search" name={"SearchBar"} value={search} onChange={handleSearchChange} required/>

                <button type="submit" onClick={StartSearch}>start search</button>

                <MostPopularProducts/>
            </div>
        );
    }
}

export default Products;
