import React, {useState} from "react";

import GetAccountById from "../ApiAccount/GetAccountById";
import axios from "axios";

function HomePage() {

    const [ID, setID] = useState('');
    const [result, setResult] = useState('');

    const handleIDChange = (e) => {
        setID(e.target.value);
        setResult(null);
    };


    const getData = () => {
        axios.get('http://localhost:8080/accounts/' + ID)
            .then(res => {
                setResult(res.data);
                console.log("res", res.data.message);
            })
            .catch(err => {
                console.log("error in request", err);
            });
    };

    if(result != null) {
        return (
            <div className="container">

                <label><b>Username</b></label>
                <input type="text" placeholder="Enter ID" name={"ID"} value={ID} onChange={handleIDChange} required/>

                <button type="submit" onClick={getData}>Login</button>
                <table>
                    <tr>
                        <td>{result.firstname}</td>
                        <td>{result.lastname} </td>
                        <td>{result.email}</td>
                        <td>{result.phoneNumber}</td>
                    </tr>
                </table>
            </div>
        );
    }
    else{
        return (
            <div className="container">

                <label><b>Username</b></label>
                <input type="text" placeholder="Enter ID" name={"ID"} value={ID} onChange={handleIDChange} required/>

                <button type="submit" onClick={getData}>Login</button>
            </div>
        );
    }
}

export default HomePage;
