import React, {useState} from 'react';

import "./Css/Login.css"
import axios from "axios";
import ReactSession from 'react-client-session';

function Login() {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [result, setResult] = useState('');
    const [error, setError] = useState('');

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleLogin = () => {
        axios({
            method: "GET",
            url: 'http://localhost:8080/accounts/login',
            data: {
                "username": username,
                "password": password
            }
        }).then(res => {
            setResult(res.data);
            console.log("res", res.data.message);

            if(result != null){
                ReactSession.set("username", "TRUE");
            }
            else{
                ReactSession.set("username", "FALSE");
            }
        }).catch(err => {
            setError(err);
            console.log("err", err);
        });
    };

    if( ReactSession.get("username") == "TRUE") {
        return (
            <div className="container">
                <label><b>Username</b></label>
                <input type="text" placeholder="Enter Username" name={"username"} value={username}
                       onChange={handleUsernameChange} required/>

                <label><b>Password</b></label>
                <input type="password" placeholder="Enter Password" name={"password"} value={password}
                       onChange={handlePasswordChange} required/>

                <button type="submit" onClick={handleLogin}>Login</button>
            </div>
        );
    }
    else if(error != null){
        return (
            <div className="container">
                <label><b>Username</b></label>
                <input type="text" placeholder="Enter Username" name={"username"} value={username}
                       onChange={handleUsernameChange} required/>

                <label><b>Password</b></label>
                <input type="password" placeholder="Enter Password" name={"password"} value={password}
                       onChange={handlePasswordChange} required/>

                <button type="submit" onClick={handleLogin}>Login</button>

                <h3> Login failed</h3>
            </div>
        );
    }
    else{
        return (
            <div className="container">
                <label><b>Username</b></label>
                <input type="text" placeholder="Enter Username" name={"username"} value={username}
                       onChange={handleUsernameChange} required/>

                <label><b>Password</b></label>
                <input type="password" placeholder="Enter Password" name={"password"} value={password}
                       onChange={handlePasswordChange} required/>

                <button type="submit" onClick={handleLogin}>Login</button>
            </div>
        );
    }
}

export default Login;
