import React, {useState} from "react";
import axios from "axios";
import DisplayProducts from "../DisplayProducts";
import product from "../../Product";

export default SearchResult;

function SearchResult({search = ""}) {

    const [productArray, updateProductArray] = useState([]);
    const [error, setError] = useState('');

    const StartSearch = () => {
        updateProductArray(null);
        setError(null);
        axios.get('http://localhost:8080/products/search/' + search)
            .then(res => {
                updateProductArray(res.data);
            })
            .catch(err => {
                setError(err.message);
            });
    };

    if(productArray != null) {
        return(
            <div className="container">
                <button type="submit" onClick={StartSearch}>start search</button>
                <DisplayProducts productsArray={productArray}/>
            </div>
        );
    }
    else if(error != null){
        return (
            <div className="container">
                <button type="submit" onClick={StartSearch}>start search</button>
                <h3> Error: {error}</h3>
            </div>
        );
    }
    else {
        return (
            <div className="container">
                <button type="submit" onClick={StartSearch}>start search</button>
            </div>
        );
    }
}
