import React, {useState} from 'react';

import "./Login.css"
import axios from "axios";

function Login() {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [result, setResult] = useState('');

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleLogin = () => {

            axios({
                method: "GET",
                url: 'http://localhost:8080/accounts/' + username + '/' + password,
                headers: {},
                data: {
                    'username': password,
                    'password': username
                },
            })
                .then(res => {
                    console.log("res", res.data.message);
                })
        .then(res => {
                setResult(res.data);
                console.log(res.data);
            })

        if(result == null) {
            return(<h2>false</h2>);
        }
        else
        {
            return(<h2>true</h2>);
        }
    };

    return (
        <div className="container">

            <label><b>Username</b></label>
            <input type="text" placeholder="Enter Username" name={"username"} value={username}  onChange={handleUsernameChange} required/>

            <label><b>Password</b></label>
            <input type="password" placeholder="Enter Password" name={"password"} value={password}  onChange={handlePasswordChange} required/>

            <button type="submit" onClick={handleLogin}>Login</button>
        </div>
    );
}

export default Login;
