import React, {useState} from "react";
function SearchBar() {

    const [SearchString, setSearchString] = useState("");
    const [SortByYear, setSortByYear] = useState(0);
    const [SortByPrice, setSortByPrice] = useState(0);
    const [SortByCondition, setSortByCondition] = useState(0);

    const handleChangeSearch = (e) => {
        setSearchString(e.target.value);
    };
    const handleChangeYear = () => {
        if(SortByYear == 1) {
            setSortByYear(0);
        }
        else {
            setSortByYear(1);
        }
        console.log(SortByYear);
    };
    const handleChangePrice = () => {
        if(SortByPrice == 1) {
            setSortByPrice(0);
        }
        else {
            setSortByPrice(1);
        }        console.log(SortByPrice);
    };
    const handleChangeCondition = () => {
        if(SortByCondition == 1) {
            setSortByCondition(0);
        }
        else {
            setSortByCondition(1);
        }        console.log(SortByCondition);
    };

    return (
        <div className="searchbar">
            <div  className="sort-options">
                <span> Sort by:</span>
                <span> Year</span>
                <input type={"checkbox"}  value={SortByYear} onChange={handleChangeYear}/>
                <span> Price</span>
                <input  type={"checkbox"}  value={SortByPrice} onChange={handleChangePrice}/>
                <span> Condition</span>
                <input type={"checkbox"} value={SortByCondition} onChange={handleChangeCondition}/>
            </div>
            <div className="search-container">
                <input type="text" placeholder="Search.." name="search" value={SearchString} onChange={handleChangeSearch}/>
                <button type="submit"> Search</button>
            </div>
        </div>
    );
}

export default SearchBar;
