import React, {useEffect, useState} from "react";
import DisplayProducts from "../DisplayProducts";
import axios from "axios";

function SearchResult({SearchString = "", SortBy = ""}) {

    useEffect(() => {
        GetSearchResult();
    },[]);

    const [productsArray, setProductsArray] = useState([]);

    const GetSearchResult =() => {
        if(SortBy != "" && SearchString != "") {
            axios.get(`http://localhost:8080/products/` + SearchString + "/" + SortBy)
                .then(res => {
                    setProductsArray(res.data);
                })
                .catch(err => {
                    console.log(err.message);
                });
        }
        else if(SearchString != "") {
            axios.get(`http://localhost:8080/products/search/` + SearchString)
                .then(res => {
                    setProductsArray(res.data);
                })
                .catch(err => {
                    console.log(err.message);
                });
        }
        else if(SortBy != "") {
            axios.get(`http://localhost:8080/products/sort/` + SortBy)
                .then(res => {
                    setProductsArray(res.data);
                })
                .catch(err => {
                    console.log(err.message);
                });
        }
        else {
            axios.get(`http://localhost:8080/products/`)
                .then(res => {
                    setProductsArray(res.data);
                })
                .catch(err => {
                    console.log(err.message);
                });
        }
    }

    return (
        <div className="container">
            <DisplayProducts productsArray={productsArray} />
        </div>
    );
}

export default SearchResult;
