import React, {useState} from "react";

function SearchBar() {

    const [SearchString, setSearchString] = useState("");
    const [SortByYear, setSortByYear] = useState(0);
    const [SortByPrice, setSortByPrice] = useState(0);
    const [SortByCondition, setSortByCondition] = useState(0);

    const handleChangeSearch = (e) => {
           setSearchString(e.innerText);
           console.log(SearchString, SortByYear, setSortByCondition, setSortByPrice);
    };
    const handleChangeYear = () => {
        if(SortByYear == 0) {
            setSortByYear(1);
        }
        else {
            setSortByYear(0);
        }
        console.log(SearchString, SortByYear, setSortByCondition, setSortByPrice);
    };
    const handleChangePrice = () => {
        if(SortByPrice == 0) {
            setSortByPrice(1);
        }
        else {
            setSortByPrice(0);
        }
        console.log(SearchString, SortByYear, setSortByCondition, setSortByPrice);
    };
    const handleChangeCondition = () => {
        if(SortByCondition == 0) {
            setSortByCondition(1);
        }
        else {
            setSortByCondition(0);
        }
        console.log(SearchString, SortByYear, setSortByCondition, setSortByPrice);
    };


        return (
            <div>
                <label> Search:</label>
                <input type={"text"} value={SearchString} onChange={handleChangeSearch}/>

                <label> Sort by:</label>
                <label> Year</label>
                <input type={"checkbox"}  value={SortByYear} onChange={handleChangeYear}/>
                <label> Price</label>
                <input type={"checkbox"}  value={SortByPrice} onChange={handleChangePrice}/>
                <label> Condition</label>
                <input type={"checkbox"} value={SortByCondition} onChange={handleChangeCondition}/>

            </div>
        );
}

export default SearchBar;
