import React, {useState} from "react";
import axios from "axios";

function Products() {

    const [search, setSearch] = useState('');
    const [SearchArray, updateSearchArray] = useState([]);
    const [MostPopularArray, updateMostPopularArray] = useState([]);
    const [error, setError] = useState('');

    const handleSearchChange = (e) => {
        setSearch(e.target.value);
    };

    const StartSearch = () => {
        updateSearchArray(null);
        setError(null);

        axios.get('http://localhost:8080/products/search/' + search)
            .then(res => {
                console.log(res.data);
                updateSearchArray(res.data);
            })
            .catch(err => {
                setError(err.message);
            });
    };

    if(SearchArray != null) {
        return (
            <div className="container">
                <label>Search:</label>
                <input type="text" placeholder="Search" name={"SearchBar"} value={search} onChange={handleSearchChange} required/>

                <button type="submit" onClick={StartSearch}>start search</button>

                <table>
                    <tr>
                        <th>name1</th>
                        <th>name2</th>
                        <th>serie</th>
                        <th>year</th>
                        <th>condition</th>
                        <th>genre</th>
                        <th>description</th>
                    </tr>
                    {SearchArray.map(product => (
                        <tr>
                            <td>{product.name1}</td>
                            <td>{product.name2} </td>
                            <td>{product.serie}</td>
                            <td>{product.year}</td>
                            <td>{product.condition}</td>
                            <td>{product.genre} </td>
                            <td>{product.description}</td>
                        </tr>)
                    )}
                </table>
            </div>
        );
    }
    else if(error != null){
        return (
            <div className="container">
                <label>Search:</label>
                <input type="text" placeholder="Search" name={"SearchBar"} value={search} onChange={handleSearchChange} required/>

                <button type="submit" onClick={StartSearch}>start search</button>

                <h3> Error: {error}</h3>
            </div>
        );
    }
    else{
        return (
            <div className="container">
                <label>Search:</label>
                <input type="text" placeholder="Search" name={"SearchBar"} value={search} onChange={handleSearchChange} required/>

                <button type="submit" onClick={StartSearch}>start search</button>

                <h3> Most popular </h3>
                <table>
                    <tr>
                        <th>name1</th>
                        <th>name2</th>
                        <th>serie</th>
                        <th>year</th>
                        <th>condition</th>
                        <th>genre</th>
                        <th>description</th>
                    </tr>
                    {MostPopularArray.map(product => (
                        <tr>
                            <td>{product.name1}</td>
                            <td>{product.name2} </td>
                            <td>{product.serie}</td>
                            <td>{product.year}</td>
                            <td>{product.condition}</td>
                            <td>{product.genre} </td>
                            <td>{product.description}</td>
                        </tr>)
                    )}
                </table>
            </div>
        );
    }
}

export default Products;
