import React, {useEffect, useState} from "react";
import "./ProdutDetails.css"

import {useParams} from "react-router-dom";
import axios from "axios";

function View_Product_Details() {

    const [product, setProduct] = useState({});
    const [error, setError] = useState("");

    useEffect(() => {
        GetProductByID();
    },[]);

    const {productId} = useParams();

    const GetProductByID =() => {
        setProduct(null);
        setError(null);
        axios.get(`http://localhost:8080/products/` + productId)
            .then(res => {
                setProduct(res.data);
            })
            .catch(err => {
                setError(err.message);
            });
    }

   if(product != null) {
       return (
           <div className="container">

               <div className="left-column">
               </div>

               <div className="right-column">

                   <div className="product-description">
                       <span>{product.name1}</span>
                       <h1>{product.name2}</h1>
                       <p>{product.discription}</p>
                   </div>

                   <div className="product-price">
                       <span>{product.Price}</span>
                       <button className="cart-btn">Add to cart</button>
                   </div>
               </div>

           </div>
       );
   }
   else if(error != null) {
       return (
           <div>
               <h1>{error}</h1>
           </div>
       );
   }
   else{
      return(
          <div>
              <h1>Something has gone wrong please try again later.</h1>
          </div>);
   }
}

export default View_Product_Details;
