import React, {useState} from "react";

function SearchBar() {

    const [SearchString, setSearchString] = useState("");
    const [SortByYear, setSortByYear] = useState(0);
    const [SortByPrice, setSortByPrice] = useState(0);
    const [SortByCondition, setSortByCondition] = useState(0);

    const handleChangeSearch = (e) => {
        setSearchString(e.target.value);
    };
    const handleChangeYear = (e) => {
        setSortByYear(e.target.value);
        console.log(SortByYear);
    };
    const handleChangePrice = () => {
        if(SortByPrice == 0) {
            setSortByPrice(1);
        }
        else {
            setSortByPrice(0);
        }
    };
    const handleChangeCondition = () => {
        if(SortByCondition == 0) {
            setSortByCondition(1);
        }
        else {
            setSortByCondition(0);
        }
    };


        return (
            <div>
                <label> Search:</label>
                <input type={"text"} onChange={handleChangeSearch}/>

                <label> Sort by:</label>
                <label> Year</label>
                <input type={"checkbox"} onChange={handleChangeYear}/>
                <label> Price</label>
                <input type={"checkbox"} onChange={handleChangePrice}/>
                <label> Condition</label>
                <input type={"checkbox"} onChange={handleChangeCondition}/>

            </div>
        );
}

export default SearchBar;
