import React, {useState} from "react";
import SearchResult from "./SearchResult";
function SearchBar() {

    const [SearchString, setSearchString] = useState("");
    const [SortBy, setSortBy] = useState("");

    const handleChangeSearch = (e) => {
        setSearchString(e.target.value);
    };
    const handleChangeYear = () => {
        setSortBy("Year");
    };
    const handleChangePrice = () => {
        setSortBy("Price");
    };
    const handleChangeCondition = () => {
        setSortBy("Condition");
    };

    return (
        <div className="searchbar">
            <div  className="sort-options">
                <span> Sort by:</span>
                <span> Year</span>
                <input type={"checkbox"} onChange={handleChangeYear}/>
                <span> Price</span>
                <input  type={"checkbox"} onChange={handleChangePrice}/>
                <span> Condition</span>
                <input type={"checkbox"} onChange={handleChangeCondition}/>
            </div>
            <div className="search-container">
                <input type="text" placeholder="Search.." name="search" value={SearchString} onChange={handleChangeSearch}/>
                <button type="submit"> Search</button>
            </div>

            <SearchResult SearchString={SearchString} SortBy={SortBy}/>
        </div>
    );
}

export default SearchBar;
