import React, {useState} from 'react';

import "./Login.css"
import axios from "axios";

const [username, setUsername] = useState('');
const [password, setPassword] = useState('');

function LoginButton() {

    const [result, setResult] = useState('');

    axios.get('http://localhost:8080/accounts/' + username + password)
        .then(res => {
            setResult(res.data);
        })

    if(result != null) {
        console.log('True');
       // return(<h2>true</h2>);
    }
    else
    {
        console.log('False');
      //  return(<h2>false</h2>);
    }
}

const handleUsernameChange = (e) => {
    setUsername(e.target.value);
};

const handlePasswordChange = (e) => {
    setPassword(e.target.value);
};

function Login() {
    return (
        <div className="container">

            <label><b>Username</b></label>
            <input type="text" placeholder="Enter Username" name={"username"} value={username}  onChange={handleUsernameChange} required/>

            <label><b>Password</b></label>
            <input type="password" placeholder="Enter Password" name={"password"} value={password}  onChange={handlePasswordChange} required/>

            <button type="submit" onClick={LoginButton}>Login</button>
        </div>
    );
}

export default Login;
